import React, { Component } from "react";
import {
  Input,
  InputLabel,
  FormControl,
  FormHelperText
} from "@material-ui/core";

class Username extends Component {
  constructor(props) {
    super(props);
    this.onChange = this.onChange.bind(this);
    this.sortErrors = this.sortErrors.bind(this);
  }

  onChange(event) {
    this.props.onUsernameChange(event.target.value);
    this.props.onClearErrorMsg("username");
  }

  sortErrors(errors, key) {
    let newArr = [];
    /*for (let error of errors) {
      if (key === error.element) {
        newArr.push(error.message);
      }
    }
    return newArr;*/
  }

  render() {
    const username = this.props.username;
    const errors = this.props.errors;

    console.log(this.props);
    return (
      <FormControl margin="normal" required>
        <InputLabel htmlFor="username">username</InputLabel>
        <Input
          id="username"
          name="username"
          value={username}
          onChange={this.onChange}
        />
        <FormHelperText id="usernameHelper">
          {this.sortErrors(errors, "username")[0]}
        </FormHelperText>
      </FormControl>
    );
  }
}

export default Username;
