import React, { Component, Fragment } from "react";
import {
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle
} from "@material-ui/core";

class NewUser extends Component {
  constructor(props) {
    super(props);
    const { parentState } = this.props.parentState;
    this.state = {
      openDialog: false
      //parentState: {parentState }
    };
    console.log(parentState);
    this.validateUser = this.validateUser.bind(this);
    this.showValidarionErrors = this.showValidarionErrors.bind(this);
    this.clearValidationErrors = this.clearValidationErrors.bind(this);
    this.validateUser = this.validateUser.bind(this);
  }

  handleClickOpen = () => {
    if (this.validateUser() === true) {
      console.log("valid");
    } else {
      console.log("not valid");
    }

    //this.registerUser();
  };

  handleClose = () => {
    this.setState({ openDialog: false });
  };

  showValidarionErrors(element, message) {
    this.props.prevState = {
      errors: [...this.props.prevState.errors, { element, message }]
    };
  }

  clearValidationErrors(element) {
    let newArr = [];
    for (let error of this.props.prevState.errors) {
      if (element !== error.element) {
        newArr.push(error);
      }
    }
    return { errors: newArr };
  }

  validateUser() {
    const username = this.state.parentState.username;

    let registrationValid = true;
    if (username === "") {
      this.showValidarionErrors(
        "username",
        "The Username Field cannot be empty"
      );
      registrationValid = false;
    }
    return registrationValid;
  }

  render() {
    return (
      <Fragment>
        <Button
          variant="contained"
          color="primary"
          type="submit"
          onClick={this.handleClickOpen}
        >
          Register
        </Button>
      </Fragment>
    );
  }
}

export default NewUser;

