import React, { Component } from "react";
import {
  Input,
  InputLabel,
  FormControl,
  FormHelperText
} from "@material-ui/core";

class Username extends Component {
  constructor(props) {
    super(props);
    this.onChange = this.onChange.bind(this);
    this.sortErrors = this.sortErrors.bind(this);
    this.checkErrors = this.checkErrors.bind(this);
  }

  onChange(event) {
    this.props.onUsernameChange(event.target.value);
    this.props.onClearErrorMsg("username");
  }

  sortErrors(errors, key) {
    let newArr = [];
    for (let error of errors) {
      if (key === error.element) {
        newArr.push(error.message);
      }
    }
    return newArr;
  }

  checkErrors(errors) {
    if (errors === null) {
      return false;
    } else {
      return true;
    }
  }

  render() {
    const username = this.props.parentState.username;
    const errors = this.props.parentState.errors;

    console.log(this.props);
    return (
      <FormControl margin="normal" required error={this.checkErrors(errors)}>
        <InputLabel htmlFor="username">username</InputLabel>
        <Input
          id="username"
          name="username"
          value={username}
          onChange={this.onChange}
        />
        <FormHelperText id="usernameHelper" error={true}>
          {this.sortErrors(errors, "username")[0]}
        </FormHelperText>
      </FormControl>
    );
  }
}

export default Username;
