import React, { Component } from "react";
import Input from "@material-ui/core/Input";
import InputLabel from "@material-ui/core/InputLabel";
import FormControl from "@material-ui/core/FormControl";

class Username extends Component {
  constructor(props) {
    super(props);
    /*this.state = {
      username: "",
      errors: []
    };*/
    this.showValidarionErrors = this.showValidarionErrors.bind(this);
    this.clearValidationErrors = this.showValidarionErrors.bind(this);
    this.onchangeUsername = this.onchangeUsername.bind(this);
  }

  showValidarionErrors(element, message) {
    this.props.prevState = {
      errors: [...prevState.errors, { element, message }]
    };
  }

  clearValidationErrors(element) {
    this.setSate(prevState => {
      let newArr = [];
      for (let error of prevState.errors) {
        if (element != error.element) {
          newArr.push(error);
        }
      }
      return { errors: newArr };
    });
  }

  onchangeUsername(event) {
    this.setSate.username({ username: event.target.value });
  }

  render() {
    return (
      <FormControl margin="normal" required>
        <InputLabel>username</InputLabel>
        <Input id="username" onChange={this.onchangeUsername} />
      </FormControl>
    );
  }
}

export default Username;
